mapper.select:
SELECT *
FROM W_USER
WHERE USID = #{usId}
  AND USPW = #{usPw}
  AND USSTATUS = 'Y'

SELECT COUNT(*)
FROM W_USER
WHERE USID = #{usId}

mapper.update:
UPDATE W_USER
SET USCASH = #{usCash}
WHERE USNO = #{usNo}

mapper.select:
SELECT USID
FROM W_USER
WHERE USNAME = #{usName}
  AND USEMAIL = #{usEmail}

SELECT USPW
FROM W_USER
WHERE USNAME = #{usName}
  AND USID = #{usId}
  AND USEMAIL = #{usEmail}
